#!/usr/bin/env python3.6#import required librariesimport requestsimport osimport timefrom selenium import webdriverfrom selenium.webdriver.support.ui import WebDriverWaitfrom selenium.webdriver import Chromeurl = "https://www.amazon.in"#print(search)driver = webdriver.Chrome()driver.wait = WebDriverWait(driver, 5) #driver.get(url)list_=[]def lookup(driver, query):	driver.get(url)	list_ = []try:    box = driver.wait.until(EC.presence_of_element_located(            (By.id, "twotabsearchbox")))    search = input("Enter the key word you want to search:")    box.click()	    button = driver.wait.until(EC.element_to_be_clickable(            (By.NAME, "nav-input")))    box.send_keys(query)    button.click()except TimeoutException:     print("Box or Button not found in Amazon.in")def looping_pages():	for i in range(2):		driver.get(url)		# print(str(i))		button = driver.find_element_by_link_text(str(i)).click()		# print(button)		def driver():		list_name = driver.find_elements_by_tag_name("h2")		data = list_name.text		list_.append(data)		price = driver.find_elements_by_id("price")		info = price.text		list_.append(info)		rating = driver.find_elements_by_class_name("a-icon-alt")		ratings = rating.text		list_.append(ratings)		time.sleep(5)def write_to_csv():	with open('amazon.csv', 'w') as csvfile:		headers = ['Product', 'Price','Rating']		writer = csv.writer(csvfile)		writer.writerow(headers)# writing "links" in column header		for i in looping_pages():			writer.writerow([i])lookup()looping_pages()write_to_csv()